import React, { useState, useEffect } from 'react';

const Dice = () => {
  const [isShuffling, setIsShuffling] = useState(false);
  const [diceFace, setDiceFace] = useState(1);

  useEffect(() => {
    let interval;
    if (isShuffling) {
      let count = 0;
      interval = setInterval(() => {
        const randomFace = Math.floor(Math.random() * 6) + 1;
        setDiceFace(randomFace);
        count++;
        if (count >= 50) { 
          clearInterval(interval);
          setIsShuffling(false);
        }
      }, 20);
    }

    return () => clearInterval(interval);
  }, [isShuffling]);

  const shuffleDice = () => {
    setIsShuffling(true);
  };

  return (
    <div>
      <div className={`dice ${isShuffling ? 'shuffling' : ''}`}>
        <img src={`./images/dice-six-faces-${diceFace}.png`} alt={`Dice face ${diceFace}`} />
      </div>
      <div>
      <button onClick={shuffleDice} disabled={isShuffling}> <img src="./images/dice-six-faces-1.png" alt="Button with Picture" style={{ maxWidth: '100%', maxHeight: '100%' }} /></button>
      <button onClick={shuffleDice} disabled={isShuffling}> <img src="./images/dice-six-faces-2.png" alt="Button with Picture" style={{ maxWidth: '100%', maxHeight: '100%' }} /></button>
      <button onClick={shuffleDice} disabled={isShuffling}> <img src="./images/dice-six-faces-3.png" alt="Button with Picture" style={{ maxWidth: '100%', maxHeight: '100%' }} /></button>
      <button onClick={shuffleDice} disabled={isShuffling}> <img src="./images/dice-six-faces-4.png" alt="Button with Picture" style={{ maxWidth: '100%', maxHeight: '100%' }} /></button>
      <button onClick={shuffleDice} disabled={isShuffling}> <img src="./images/dice-six-faces-5.png" alt="Button with Picture" style={{ maxWidth: '100%', maxHeight: '100%' }} /></button>
      <button onClick={shuffleDice} disabled={isShuffling}> <img src="./images/dice-six-faces-6.png" alt="Button with Picture" style={{ maxWidth: '100%', maxHeight: '100%' }} /></button>
      </div>

    </div>


  );
};

export default Dice;
