import React, { useState, useEffect } from 'react';

const Dice = () => {
  const [isShuffling, setIsShuffling] = useState(false);
  const [diceFace, setDiceFace] = useState(1);
  const [selectedFace, setSelectedFace] = useState(null);
  const [trials, setTrials] = useState(0);

  useEffect(() => {
    let interval;
    if (isShuffling) {
      let count = 0;
      interval = setInterval(() => {
        const randomFace = Math.floor(Math.random() * 6) + 1;
        setDiceFace(randomFace);
        count++;
        if (count >= 50) {
          clearInterval(interval);
          setIsShuffling(false);
          checkMatch(selectedFace, randomFace);
        }
      }, 20);
    }
    return () => clearInterval(interval);
  }, [isShuffling, selectedFace]);

  const selectFace = (face) => {
    if (!isShuffling) {
      setSelectedFace(face);
      setTrials(trials + 1);
      setIsShuffling(true); // Start rolling after selecting a face
    }
  };

  const checkMatch = (selected, random) => {
    if (selected === random) {
      alert('Hurray! You matched the dice face.');
    }
  };

  return (
    <div className='random-dice'>
      <div className={`dice ${isShuffling ? 'shuffling' : ''}`}>
        <img src={`./images/dice-six-faces-${diceFace}.png`} alt={`Dice face ${diceFace}`} />
      </div>
      <br />
      <p>Select your dice</p>
      <div>
        {[1, 2, 3, 4, 5, 6].map(face => (
          <button className='selection-dice' key={face} onClick={() => selectFace(face)} disabled={isShuffling}>
            <img src={`./images/dice-six-faces-${face}.png`} alt={`Dice face ${face}`} style={{ maxWidth: '100%', maxHeight: '100%' }} />
          </button>
        ))}
      </div>
      <p>No. of Trials: {trials}</p>
    </div>
  );
};

export default Dice;
