import React, { useState, useEffect } from 'react';


const Dice = () => {
  const [isShuffling, setIsShuffling] = useState(false);
  const [diceFace, setDiceFace] = useState(1);

  useEffect(() => {
    let interval;
    if (isShuffling) {
      let count = 0;
      interval = setInterval(() => {
        const randomFace = Math.floor(Math.random() * 6) + 1;
        setDiceFace(randomFace);
        count++;
        if (count >= 100) { // Shuffle for 2 seconds (100 iterations at 20ms each)
          clearInterval(interval);
          setIsShuffling(false);
          const finalRandomFace = Math.floor(Math.random() * 6) + 1;
          setDiceFace(finalRandomFace);
        }
      }, 20);
    }

    return () => clearInterval(interval);
  }, [isShuffling]);

  const shuffleDice = () => {
    setIsShuffling(true);
  };

  return (
    <div>
      <div className={`dice ${isShuffling ? 'shuffling' : ''}`}>
        <img src={`./images/dice-six-faces-${diceFace}.png`} alt={`Dice face ${diceFace}`} />
      </div>
      <button onClick={shuffleDice} disabled={isShuffling}>Roll Dice</button>
    </div>
  );
};

export default Dice;
